function createListItem(data) {
  let $tmpl = $("#art-galley-template").clone().removeAttr("id");
  // console.log($tmpl);

  $tmpl
    .find(".exhibit-image")
    .attr("src", "https://soma.kspo.or.kr/" + data["dspyimageurl"]);
  // console.log($tmpl);
  $tmpl
    .find(".exhibit-description")
    .html(data["dspynm"]);

  return $tmpl;
}

function loadApiData(r) {
  let = $list = $('.art-galley-container');

  for (let i = 0; i < r.length; i++) {
    let data = r[i];
    let $item = createListItem(data);
    $list.append($item);
  }
}
